function show_inner_banner_block(name, url) {
	if( typeof(name) !== 'undefined') {
		if( $('.'+name+'_inner_banner').size() > 0) {
			$('.'+name+'_inner_banner').show();
		}
	}

	if( typeof(url) !== 'undefined') {
		$('.'+name+'_inner_banner .block_url').attr('href', url);
	}
}

